                "explanation": "Handles all authentication-related routes. Implements OTP (One-Time Password) login flow, verifies credentials, and issues JWT tokens for session management. Includes routes for login and verification.",
                "content": "import { Router } from \"express\";\r\nimport { supabase } from \"../lib/supabase\";\r\nimport { generateOtp, hashOtp, verifyOtp, otpExpiry } from \"../lib/otp\";\r\nimport { sendOtpEmail } from \"../lib/mailer\";\r\nimport jwt from \"jsonwebtoken\";\r\n\r\nconst router = Router();\r\n\r\nconst JWT_SECRET = process.env.JWT_SECRET;\r\nif (!JWT_SECRET) {\r\n  throw new Error(\"JWT_SECRET missing\");\r\n}\r\n\r\nfunction now(): number {\r\n  return Math.floor(Date.now() / 1000);\r\n}\r\n\r\n//request otp\r\nrouter.post(\"/request-otp\", async (req, res) => {\r\n  try {\r\n    const { email } = req.body;\r\n\r\n    if (!email || typeof email !== \"string\") {\r\n      return res.status(400).json({ error: \"Invalid email\" });\r\n    }\r\n\r\n    const { data: existing, error: fetchError } = await supabase\r\n      .from(\"otp_codes\")\r\n      .select(\"expires_at\")\r\n      .eq(\"email\", email)\r\n      .limit(1);\r\n\r\n    if (fetchError) {\r\n      console.error(\"OTP fetch error:\", fetchError);\r\n      return res.status(500).json({ error: \"Database error\" });\r\n    }\r\n\r\n    if (\r\n      existing &&\r\n      existing.length > 0 &&\r\n      existing[0].expires_at - now() > 240\r\n    ) {\r\n      return res.status(429).json({\r\n        error: \"Please wait before requesting another OTP\",\r\n      });\r\n    }\r\n\r\n    const otp = generateOtp();\r\n    const otpHash = hashOtp(otp);\r\n\r\n    const { error: insertError } = await supabase\r\n      .from(\"otp_codes\")\r\n      .upsert(\r\n        {\r\n          email,\r\n          otp_hash: otpHash,\r\n          expires_at: otpExpiry(),\r\n        },\r\n        { onConflict: \"email\" }\r\n      );\r\n\r\n    if (insertError) {\r\n      console.error(\"OTP insert failed:\", insertError);\r\n      return res.status(500).json({ error: \"Failed to store OTP\" });\r\n    }\r\n\r\n    await sendOtpEmail(email, otp);\r\n\r\n    return res.json({ success: true });\r\n  } catch (err) {\r\n    console.error(\"Request OTP error:\", err);\r\n    return res.status(500).json({ error: \"Internal server error\" });\r\n  }\r\n});\r\n\r\n//verify otp\r\nrouter.post(\"/verify-otp\", async (req, res) => {\r\n  try {\r\n    const { email, otp } = req.body;\r\n\r\n    if (!email || !otp) {\r\n      return res.status(400).json({ error: \"Missing fields\" });\r\n    }\r\n\r\n    const { data: records, error } = await supabase\r\n      .from(\"otp_codes\")\r\n      .select(\"*\")\r\n      .eq(\"email\", email)\r\n      .limit(1);\r\n\r\n    if (error) {\r\n      console.error(\"OTP fetch error:\", error);\r\n      return res.status(500).json({ error: \"Database error\" });\r\n    }\r\n\r\n    if (!records || records.length === 0) {\r\n      return res.status(400).json({ error: \"OTP not found\" });\r\n    }\r\n\r\n    const record = records[0];\r\n\r\n    if (record.expires_at < now()) {\r\n      return res.status(400).json({ error: \"OTP expired\" });\r\n    }\r\n\r\n    if (!verifyOtp(otp, record.otp_hash)) {\r\n      return res.status(400).json({ error: \"Invalid OTP\" });\r\n    }\r\n\r\n    const { data: user, error: userError } = await supabase\r\n      .from(\"users\")\r\n      .upsert({ email }, { onConflict: \"email\" })\r\n      .select()\r\n      .single();\r\n\r\n    if (userError || !user) {\r\n      console.error(\"User upsert error:\", userError);\r\n      return res.status(500).json({ error: \"Failed to create user\" });\r\n    }\r\n\r\n    await supabase.from(\"otp_codes\").delete().eq(\"email\", email);\r\n\r\n    const token = jwt.sign(\r\n      { user_id: user.id, email: user.email },\r\n      JWT_SECRET,\r\n      { expiresIn: \"7d\" }\r\n    );\r\n\r\n    return res.json({\r\n      token,\r\n      user: {\r\n        id: user.id,\r\n        email: user.email,\r\n      },\r\n    });\r\n  } catch (err) {\r\n    console.error(\"Verify OTP error:\", err);\r\n    return res.status(500).json({ error: \"Internal server error\" });\r\n  }\r\n});\r\n\r\nexport default router;\r\n"
